    max_mistakes = available_time / mistake_time

    for i in range(int(log(max_mistakes, 2)), -1, -1):
        mistakes = 2 ** i
        total_time = 0
        for j in range(1, int(mistakes) + 1):
            total_time += mistake_time * j * 2 ** (j - 1)
        if total_time <= available_time:
            return int(mistakes)
    return 0

