def is_alt(s):
    vowels = "aeiou"

    # check if string is empty
    if not s:
        return True

    # check if first character is a vowel
    if s[0] in vowels:
        start_with_vowel = True
    else:
        start_with_vowel = False

    # check if characters alternate between vowels and consonants
    for i in range(1, len(s)):
        if s[i] in vowels:
            if start_with_vowel:
                return False
        else:
            if not start_with_vowel:
                return False

        # update start_with_vowel
        start_with_vowel = not start_with_vowel

    return True
