def is_alt(s): 
    if len(s) <= 1:
        return True
    vowels = 'aeiou'
    prev_is_vowel = s[0] in vowels
    for char in s[1:]:
        if (prev_is_vowel and char in vowels) or (not prev_is_vowel and char.isalpha() and char not in vowels):
            return False
        prev_is_vowel = char in vowels
    return True
