def is_alt(s): 
    vowels = 'aeiou'
    res = []
    for char in s:
        if char in vowels:
            res.append(1)
        else:
            res.append(0)
    for i in range(len(res)-1):
        if res[i] == res[i+1]:
            return False
    return True

def is_alt(s): 
    if len(s) <= 1:
        return True
    vowels = 'aeiou'
    prev_was_vowel = s[0] in vowels
    for char in s[1:]:
        if char in vowels == prev_was_vowel:
            return False
        prev_was_vowel = char in vowels
    return True

