        return True

    # Check if the string has alternating vowels and consonants
    for i in range(len(s)-1):
        if (s[i] in vowels and s[i+1] in vowels) or (s[i] in consonants and s[i+1] in consonants):
            return False

    return True

import re

def is_alt(s):
    vowels = 'aeiou'
    consonants = 'bcdfghjklmnpqrstvwxyz'

    # Check if the string contains any character other than vowels and consonants
    if re.search('[^aeioubcdfghjklmnpqrstvwxyz]', s):
        return False

    # Check if the characters are in alternate order
    for i in range(1, len(s)):
        if (s[i] in vowels and s[i-1] in vowels) or (s[i] in consonants and s[i-1] in consonants):
            return False

    return True
