    consonant_positions = [match.start() for match in consonant_matches]

    vowel_positions.sort()
    consonant_positions.sort()

    for i in range(len(vowel_positions) - 1):
        if vowel_positions[i] + 1 != vowel_positions[i + 1] and vowel_positions[i] + 1 in consonant_positions:
            continue
        else:
            return False
    for i in range(len(consonant_positions) - 1):
        if consonant_positions[i] + 1 != consonant_positions[i + 1] and consonant_positions[i] + 1 in vowel_positions:
            continue
        else:
            return False

    return True

