def is_alt(s):
    vowels = re.compile(r'[aeiou]')
    consonants = re.compile(r'[bcdfghjklmnpqrstvwxyz]')
    vowel_count = 0
    consonant_count = 0
    for char in s:
        if vowels.match(char):
            vowel_count += 1
            if vowel_count > 1 and consonant_count == 0:
                return False
            consonant_count = 0
        elif consonants.match(char):
            consonant_count += 1
            if consonant_count > 1 and vowel_count == 0:
                return False
            vowel_count = 0
    return True
