    consonants = ['b', 'k', 'x', 'z', 'n', 'h', 'd', 'c', 'w', 'g', 'p', 'v', 'j', 'q', 't', 's', 'r', 'l', 'm', 'f']

    decoded = ""
    for char in code:
        if char.lower() in vowels:
            if char.isupper():
                decoded += vowels[(vowels.index(char.lower()) + 3) % len(vowels)].upper()
            else:
                decoded += vowels[(vowels.index(char.lower()) + 3) % len(vowels)]
        elif char.lower() in consonants:
            if char.isupper():
                decoded += consonants[(consonants.index(char.lower()) + 10) % len(consonants)].upper()
            else:
                decoded += consonants[(consonants.index(char.lower()) + 10) % len(consonants)]
        else:
            decoded += char

    return decoded

