    for i in range(len(journey)):
        if isinstance(journey[i], int):
            if is_previous_bus:
                total_cost += bus_fare
                is_previous_bus = False
            else:
                total_cost += bus_fare * 2
                is_previous_bus = True
        else:
            total_cost += tube_fare

    return '£{:.2f}'.format(total_cost)

def london_city_hacker(journey):
    tube_fare = 2.40
    bus_fare = 1.50
    total_cost = 0

    for i in range(len(journey)):
        if isinstance(journey[i], int):
            if i == 0 or isinstance(journey[i-1], int):
                total_cost += bus_fare
            else:
                total_cost += 2*bus_fare
        else:
            total_cost += tube_fare

    return '£{:.2f}'.format(total_cost)
