def london_city_hacker(journey): 
    tube_cost = 2.40
    bus_cost = 1.50

    total_cost = 0
    i = 0

    while i < len(journey):
        if isinstance(journey[i], int):
            # check if there are more adjacent bus journeys
            if i < len(journey) - 1 and isinstance(journey[i+1], int):
                total_cost += bus_cost
                i += 2
            else:
                total_cost += bus_cost
                i += 1
        else:
            total_cost += tube_cost
            i += 1

    return '£' + '{:.2f}'.format(total_cost)
