        if isinstance(journey[i], int):
            total_cost += 1.5
            adjacent_buses += 1
        else:
            if adjacent_buses >= 2:
                total_cost -= 1.5
            total_cost += 2.4
            adjacent_buses = 0

    if adjacent_buses >= 2:
        total_cost -= 1.5

    return '£{:.2f}'.format(total_cost)
