    tube_journeys = [i for i in journey if isinstance(i, str)]

    bus_sets = 0
    for i in range(len(bus_journeys) - 1):
        if i % 2 == 0 and bus_journeys[i + 1] != bus_journeys[i]:
            bus_sets += 1

    bus_sets = max(bus_sets, (len(bus_journeys) - bus_sets) // 2) + (len(bus_journeys) % 2)

    total_cost = len(tube_journeys) * 2.40 + bus_sets * 1.50
    return '£' + '{:.2f}'.format(total_cost)

