def london_city_hacker(journey):
    tube_fare = 2.40
    bus_fare = 1.50
    cost = 0
    bus_capped = 0
    for i in range(len(journey)):
        if isinstance(journey[i], str):
            cost += tube_fare
        elif isinstance(journey[i], int):
            bus_capped += bus_fare
            if i < len(journey) - 1 and isinstance(journey[i + 1], int):
                bus_capped += bus_fare
            else:
                cost += min(bus_capped, bus_fare)
                bus_capped = 0
    return round(cost + bus_capped, 2)

