def london_city_hacker(journey):
    cost = 0
    bus_cost = 0
    for element in journey:
        if type(element) == str:
            cost += 2.4
        elif type(element) == int:
            bus_cost += element
            if bus_cost > 1:
                cost += 1.5 * (bus_cost // 1) + 0.5 * (bus_cost % 1 > 0)
                bus_cost = 0
            else:
                cost += 1.5
    if bus_cost > 0:
        cost += 1.5
    return '£{:.2f}'.format(cost)

