    tube_count = 0
    bus_count = 0
    for i in range(len(journey)):
        if isinstance(journey[i], str):
            tube_count += 1
            cost += 2.40
        else:
            bus_count += 1
            cost += 1.50
        if bus_count >= 2 and isinstance(journey[i-1], int):
            cost -= 1.50
            bus_count -= 1
    return f'£{cost:.2f}'

