def find_children(dancing_brigade):
    mothers = []
    children = {}

    for char in dancing_brigade:
        if char.isupper():
            mothers.append(char)
        else:
            if char not in children:
                children[char] = 0
            children[char] += 1

    result = []
    for mother in sorted(mothers):
        result.append(mother)
        if mother.lower() in children:
            result.append(mother.lower() * children[mother.lower()])

    return ''.join(result)
