def find_children(dancing_brigade):
    mothers = sorted([mother for mother in dancing_brigade if mother.isupper()])
    children = [child for child in dancing_brigade if child.islower()]
    for mother in mothers:
        children.sort(key=lambda child: mother.lower() == child)
    result = []
    for mother in mothers:
        result.append(mother)
        result.extend([child for child in children if child == mother.lower()])
    return ''.join(result)

